function ver_todos_filtros(valor,id)
{
if (id == "marcas") {
$("#marcas_top").css("display", "none");
$("#marcas_alfabetic").css("display", "block");
id = "marcas_alfabetic";
$("#marcas").css("height", "auto");
$("#marcas").css("max-height", "none");
$("#marcas").css("overflow", "auto");
}

$(valor).css("display", "none");
$("#" + id).css("height", "auto");
$("#" + id).css("max-height", "none");
$("#" + id).css("overflow", "auto");
$(valor).siblings('a').css("display", "block");
}

function ocultar_todos_filtros(valor,id)
{
if(id=="marcas"){
$("#marcas_top").css("display","block");
$("#marcas_alfabetic").css("display","none");
id="marcas_top";
$("#marcas").css("height","auto");
$("#marcas").css("max-height","300px");
$("#marcas").css("overflow","hidden");
}

$(valor).css("display","none");
$("#"+id).css("height","auto");
$("#"+id).css("max-height","300px");
$("#"+id).css("overflow","hidden");
$(valor).siblings('a').css("display","block");
var strAncla=$("#"+id); //id del ancla

