div.script:
//INDEX PAGE FEATURED ITEMS
var featSwiper = new Swiper ('.featured-items', {
slidesPerView: 4,
navigation: {
prevEl: '.feat-prev',
nextEl: '.feat-next'
}
});

//PDP RECOMMENDED ITEMS
var recommendedSwiper = new Swiper ('.recommended-items', {
slidesPerView: 4,
navigation: {
prevEl: '.recommended-prev',
nextEl: '.recommended-next'
}
});

function isReadyToSubmit(status){
status =  (typeof status !== 'undefined') ?status:'valid';
var currentState = true;
if(status=='init'){
//Disable submit button on init load
currentState = false;
}
js('input[data-type="submit"]').prop("disabled",!currentState);
};
window.addEventListener('load', function() {
isReadyToSubmit('init');
}, true);
